package com.nisum.corejava.controlstructure.examples;

public class Switch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a = 50;
		switch (a) {
		case 10:
			System.out.println("if a is 10");
			break;
		case 20:
			System.out.println("if a is 20");
			break;
		case 30:
			System.out.println("if a is 30");
			break;
		case 4:
			System.out.println("if a is 40");
			break;
		case 50:
			System.out.println("if a is 50");
			break;
		default:
			System.out.println("if the value dosent exists");

		}

	}

}
